import React, { useState } from 'react';
import axios from '../services/axios';
import { useNavigate } from 'react-router-dom';

const Project = () => {
    const [projectData, setProjectData] = useState({ projectName: '', password: '' });
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        setProjectData({ ...projectData, [e.target.name]: e.target.value });
    };

    const handleCreateProject = async () => {
        try {
            // Get token from localStorage
            const token = localStorage.getItem('token');
            
            // If token exists, include it in the headers
            const config = {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            };

            // Send the request to create the project with the token
            await axios.post('/projects/create', projectData, config);
            navigate('/editor');  // Redirect to editor page after project creation
        } catch (error) {
            console.error('Error creating project', error);
        }
    };

    const handleJoinProject = async () => {
        try {
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            };

            // Send the request to join the project with the token
            await axios.post('/projects/join', projectData, config);
            navigate('/editor');  // Redirect to editor page after joining project
        } catch (error) {
            console.error('Error joining project', error);
        }
    };

    return (
        <div>
            <h2>Create or Join a Project</h2>
            <input
                type="text"
                name="projectName"
                placeholder="Project Name"
                value={projectData.projectName}
                onChange={handleInputChange}
                required
            />
            <input
                type="password"
                name="password"
                placeholder="Password"
                value={projectData.password}
                onChange={handleInputChange}
                required
            />
            <button onClick={handleCreateProject}>Create Project</button>
            <button onClick={handleJoinProject}>Join Project</button>
        </div>
    );
};

export default Project;
