import axios from 'axios';

// Set up axios instance
const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';  // Adjust API URL if not using local

const axiosInstance = axios.create({
    baseURL: apiUrl,
    timeout: 10000,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Add a request interceptor to include the JWT token in the Authorization header for every request
axiosInstance.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');  // Retrieve token from localStorage
    if (token) {
        config.headers['Authorization'] = `Bearer ${token}`;  // Attach token to request headers
    }
    return config;
}, (error) => {
    return Promise.reject(error);  // Handle error if any during the request
});

export default axiosInstance;
