import React, { useState } from 'react';
import axios from '../services/axios';  // Ensure this points to the correct axios instance
import { useNavigate } from 'react-router-dom';  // Used to navigate between pages

const Signup = () => {
    const [formData, setFormData] = useState({ name: '', email: '', password: '' });
    const [error, setError] = useState('');  // To display error messages from API
    const navigate = useNavigate();  // Used for navigation after sign-up

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('/auth/signup', formData);  // Sending data to backend
            localStorage.setItem('token', response.data.token);  // Store token on successful signup
            navigate('/project');  // Redirect to project creation/join page
        } catch (error) {
            setError('Signup failed: ' + error.response?.data.error || error.message);  // Show error if signup fails
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display error messages */}
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleInputChange}
                    required
                />
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default Signup;
