// src/components/ProjectPage.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ProjectPage = ({ setProject }) => {
  const [projectName, setProjectName] = useState("");
  const [projectCode, setProjectCode] = useState("");
  const [projectId, setProjectId] = useState("");
  const navigate = useNavigate();  // Use useNavigate instead of useHistory

  const createProject = async () => {
    try {
      const res = await axios.post("http://localhost:5000/projects/create", {
        projectName,
        projectCode,
      });
      setProject(res.data.project);
      navigate("/editor"); // Navigate to the editor page
    } catch (err) {
      console.error("Error creating project:", err);
    }
  };

  const joinProject = async () => {
    try {
      const res = await axios.post("http://localhost:5000/projects/join", {
        projectId,
      });
      setProject(res.data.project);
      navigate("/editor"); // Navigate to the editor page
    } catch (err) {
      console.error("Error joining project:", err);
    }
  };

  return (
    <div>
      <h1>Create or Join a Project</h1>
      <div>
        <input
          type="text"
          placeholder="Project Name"
          value={projectName}
          onChange={(e) => setProjectName(e.target.value)}
        />
        <textarea
          placeholder="Initial Project Code"
          value={projectCode}
          onChange={(e) => setProjectCode(e.target.value)}
        />
        <button onClick={createProject}>Create Project</button>
      </div>
      <hr />
      <div>
        <input
          type="text"
          placeholder="Project ID"
          value={projectId}
          onChange={(e) => setProjectId(e.target.value)}
        />
        <button onClick={joinProject}>Join Project</button>
      </div>
    </div>
  );
};

export default ProjectPage;
