// src/components/EditorPage.js (Editor that receives project data)
import React from "react";
import MonacoEditor from "@monaco-editor/react";
import { useParams } from "react-router-dom";

const EditorPage = ({ project }) => {
  return (
    <div>
      <h1>Editor for Project: {project?.projectName}</h1>
      <MonacoEditor
        height="90vh"
        defaultValue={project?.projectCode}
        language="javascript"
        theme="vs-dark"
        options={{ selectOnLineNumbers: true }}
      />
    </div>
  );
};

export default EditorPage;
