import React, { useState, useEffect } from 'react';
import { Editor } from '@monaco-editor/react';
import socket from '../services/socket';
import axios from '../services/axios';
import Chat from './Chat';
import { useLocation } from 'react-router-dom';  // Import useLocation to get data passed from Project.js

const CodeEditor = () => {
    const location = useLocation();  // Get data passed when navigating
    const { projectName, pjpassword } = location.state || {};  // Extract project name and password from state

    const [code, setCode] = useState('');  // Store the project code
    const [language, setLanguage] = useState('javascript');  // Default language for the editor
    const [isSaved, setIsSaved] = useState(false);  // Track whether code is saved

    // Fetch the project code on mount using project name and password
    useEffect(() => {
        if (!projectName || !pjpassword) {
            alert('Project name or password is missing.');
            return;
        }

        const fetchProjectCode = async () => {
            try {
                const response = await axios.get(`/projects/${projectName}/${pjpassword}`);
                if (response.data.code) {
                    setCode(response.data.code);  // Set the project code if available
                } else {
                    console.error('No code found for the project.');
                }
            } catch (error) {
                console.error('Failed to fetch project code:', error.message);
            }
        };
        fetchProjectCode();
    }, [projectName, pjpassword]);

    // Listen for real-time code updates from other users
    useEffect(() => {
        socket.on('receiveCode', (codeData) => {
            setCode(codeData.code);  // Update code when other users make changes
        });

        return () => {
            socket.off('receiveCode');
        };
    }, []);

    // Emit real-time code changes to the backend
    const handleCodeChange = (newCode) => {
        setCode(newCode);
        socket.emit('codeChange', { code: newCode, projectName, pjpassword });
    };

    // Save code to the project
    const saveCode = async () => {
        if (!projectName || !pjpassword) {
            alert('Project name or password is missing.');
            return;
        }
        try {
            await axios.post(`/projects/saveCode`, { projectName, pjpassword, code });
            setIsSaved(true);
            alert('Code saved successfully');
        } catch (error) {
            console.error('Error saving code:', error.message);
            alert('Failed to save code');
        }
    };

    // Unsave code and revert to the original code
    const unsaveCode = async () => {
        if (!projectName || !pjpassword) {
            alert('Project name or password is missing.');
            return;
        }
        try {
            await axios.post(`/projects/unsaveCode`, { projectName, pjpassword });
            setIsSaved(false);
            alert('Code reverted to original');
        } catch (error) {
            console.error('Error reverting code:', error.message);
            alert('Failed to revert code');
        }
    };

    return (
        <div className="editor-container">
            <div className="editor-and-chat">
                <div style={{ flex: 1 }}>
                    <Editor
                        height="80vh"
                        defaultLanguage={language}
                        value={code}
                        theme="vs-dark"
                        onChange={handleCodeChange}
                        options={{
                            fontSize: 14,
                            automaticLayout: true,
                        }}
                    />
                    <div style={{ marginTop: '10px' }}>
                        <button onClick={saveCode} disabled={isSaved} style={{ padding: '10px', marginRight: '10px' }}>
                            Save Code
                        </button>
                        <button onClick={unsaveCode} disabled={!isSaved} style={{ padding: '10px' }}>
                            Unsave Code
                        </button>
                    </div>
                </div>
                <div style={{ flex: 0.4, marginTop: '20px' }}>
                    <Chat />  {/* Display the chat component */}
                </div>
            </div>
        </div>
    );
};

export default CodeEditor;
