import React, { useState, useEffect } from 'react';
import { Editor } from '@monaco-editor/react';  // Import the Monaco editor
import socket from '../services/socket';  // Ensure the socket is set up properly
import axios from '../services/axios';  // Import Axios to make API requests
import Chat from './Chat';  // Import the Chat component

const CodeEditor = ({ projectId }) => {
    const [code, setCode] = useState('');  // Store the code
    const [language, setLanguage] = useState('javascript');  // Default language for the editor
    const [isSaved, setIsSaved] = useState(false);  // To track if the code is saved

    // Fetch the project code when the projectId changes
    useEffect(() => {
        const fetchProjectCode = async () => {
            if (!projectId) {
                console.error('Project ID is missing');
                return;
            }

            try {
                const response = await axios.get(`/projects/${projectId}/code`);
                setCode(response.data.code || '');  // Set the project code if available
            } catch (error) {
                console.error('Failed to fetch project code:', error.message);
            }
        };
        fetchProjectCode();
    }, [projectId]);

    // Listen for real-time code updates from other users
    useEffect(() => {
        socket.on('receiveCode', (codeData) => {
            setCode(codeData.code);  // Update the code from other users
        });

        return () => {
            socket.off('receiveCode');
        };
    }, []);

    // Emit real-time code changes to the backend
    const handleCodeChange = (newCode) => {
        setCode(newCode);
        socket.emit('codeChange', { code: newCode });
    };

    // Save code to the project
    const saveCode = async () => {
        if (!projectId) {
            alert('Project ID is missing!');
            return;
        }

        try {
            await axios.post(`/projects/saveCode/${projectId}`, { code });
            setIsSaved(true);  // Set the code as saved
            alert('Code saved successfully');
        } catch (error) {
            console.error('Error saving code:', error.message);
            alert('Failed to save code');
        }
    };

    // Unsave code and revert to the original code
    const unsaveCode = async () => {
        if (!projectId) {
            alert('Project ID is missing!');
            return;
        }

        try {
            await axios.post(`/projects/unsaveCode/${projectId}`);
            setIsSaved(false);  // Set the code as unsaved
            alert('Code reverted to the original');
        } catch (error) {
            console.error('Error reverting code:', error.message);
            alert('Failed to revert code');
        }
    };

    return (
        <div className="editor-container">
            <div className="editor-and-chat">
                <div style={{ flex: 1 }}>
                    <Editor
                        height="80vh"
                        defaultLanguage={language}
                        value={code}
                        theme="vs-dark"  // VS Code dark theme
                        onChange={handleCodeChange}
                        options={{
                            fontSize: 14,
                            automaticLayout: true,
                        }}
                    />
                    <div style={{ marginTop: '10px' }}>
                        <button onClick={saveCode} disabled={isSaved} style={{ padding: '10px', marginRight: '10px' }}>
                            Save Code
                        </button>
                        <button onClick={unsaveCode} disabled={!isSaved} style={{ padding: '10px' }}>
                            Unsave Code
                        </button>
                    </div>
                </div>
                <div style={{ flex: 0.4, marginTop: '20px' }}>
                    <Chat />  {/* Display the chat component */}
                </div>
            </div>
        </div>
    );
};

export default CodeEditor;
