import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Signup from './components/Signup';
import Login from './components/Login';
import Project from './components/Project';
import Editor from './components/Editor';
import { getToken } from './services/auth';  // This service should check if the user is authenticated

const App = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);  // Track authentication status

    useEffect(() => {
        // Check if user is authenticated by checking if a token exists
        const token = getToken();
        if (token) {
            setIsAuthenticated(true);
        }
    }, []);

    return (
        <Router>
            <Routes>
                {/* Signup route */}
                <Route path="/signup" element={<Signup />} />

                {/* Login route */}
                <Route path="/login" element={<Login />} />

                {/* Project creation/joining page */}
                <Route path="/project" element={isAuthenticated ? <Project /> : <Navigate to="/login" />} />

                {/* Editor and chat page */}
                <Route path="/editor" element={isAuthenticated ? <Editor /> : <Navigate to="/login" />} />

                {/* Redirect to login by default */}
                <Route path="/" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
