import React, { useState, useEffect } from 'react';
import { Editor } from '@monaco-editor/react';
import { useLocation } from 'react-router-dom';
import socket from '../services/socket';
import axios from '../services/axios';
import Chat from './Chat';

const CodeEditor = () => {
    const location = useLocation();
    const { projectName, password } = location.state || {};  // Get projectName and password from the state
    const [code, setCode] = useState('');
    const [language, setLanguage] = useState('javascript');
    const [isSaved, setIsSaved] = useState(false);

    useEffect(() => {
        const fetchProjectCode = async () => {
            if (!projectName || !password) {
                console.error('Project name or password is missing.');
                return;
            }

            try {
                const response = await axios.get(`/projects/${projectName}/${password}`);
                setCode(response.data.code || '');
            } catch (error) {
                console.error('Failed to fetch project code:', error.message);
            }
        };

        fetchProjectCode();
    }, [projectName, password]);

    useEffect(() => {
        socket.on('receiveCode', (codeData) => {
            setCode(codeData.code);
        });

        return () => {
            socket.off('receiveCode');
        };
    }, []);

    const handleCodeChange = (newCode) => {
        setCode(newCode);
        socket.emit('codeChange', { code: newCode });
    };

    const saveCode = async () => {
        try {
            await axios.post('/projects/saveCode', { projectName, password, code });
            setIsSaved(true);
            alert('Code saved successfully');
        } catch (error) {
            console.error('Error saving code:', error.message);
            alert('Failed to save code');
        }
    };

    const unsaveCode = async () => {
        try {
            await axios.post('/projects/unsaveCode', { projectName, password });
            setIsSaved(false);
            alert('Code reverted to original');
        } catch (error) {
            console.error('Error reverting code:', error.message);
            alert('Failed to revert code');
        }
    };

    return (
        <div className="editor-container">
            <div className="editor-and-chat">
                <div style={{ flex: 1 }}>
                    <Editor
                        height="80vh"
                        defaultLanguage={language}
                        value={code}
                        theme="vs-dark"
                        onChange={handleCodeChange}
                        options={{
                            fontSize: 14,
                            automaticLayout: true,
                        }}
                    />
                    <div style={{ marginTop: '10px' }}>
                        <button onClick={saveCode} disabled={isSaved} style={{ padding: '10px', marginRight: '10px' }}>
                            Save Code
                        </button>
                        <button onClick={unsaveCode} disabled={!isSaved} style={{ padding: '10px' }}>
                            Unsave Code
                        </button>
                    </div>
                </div>
                <div style={{ flex: 0.4, marginTop: '20px' }}>
                    <Chat />
                </div>
            </div>
        </div>
    );
};

export default CodeEditor;
