import React, { useState } from 'react';
import axios from '../services/axios';  // Ensure axios is correctly set up for API calls
import { useNavigate } from 'react-router-dom';  // For navigation after project creation/joining

const Project = () => {
    const [projectData, setProjectData] = useState({ projectName: '', pjpassword: '' });  // Changed to pjpassword
    const [error, setError] = useState('');  // Error state for displaying error messages
    const navigate = useNavigate();  // For redirecting after project creation or joining

    const handleInputChange = (e) => {
        setProjectData({ ...projectData, [e.target.name]: e.target.value });
    };

    // Validate if projectName and pjpassword are not empty
    const validateProjectData = () => {
        if (!projectData.projectName || !projectData.pjpassword) {
            setError('Project name and password are required');
            return false;
        }
        setError('');  // Clear the error if validation passes
        return true;
    };

    // Create Project (with token in the header)
    const handleCreateProject = async () => {
        if (!validateProjectData()) return;  // Check if project data is valid before proceeding

        const token = localStorage.getItem('token');  // Get the token from local storage
        const config = {
            headers: {
                'Authorization': `Bearer ${token}`,  // Correct token syntax (using backticks)
            },
        };

        try {
            // Make the POST request to create the project
            const response = await axios.post('/projects/create', projectData, config);
            console.log('Project created successfully:', response.data);
            navigate('/editor');  // Redirect to the editor after successful project creation
        } catch (error) {
            console.error('Error creating project', error);
            setError(error.response?.data?.error || 'Error creating project');  // Set error if project creation fails
        }
    };

    // Join Project (with token in the header)
    const handleJoinProject = async () => {
        if (!validateProjectData()) return;  // Check if project data is valid before proceeding

        const token = localStorage.getItem('token');  // Get the token from local storage
        const config = {
            headers: {
                'Authorization': `Bearer ${token}`,  // Correct token syntax (using backticks)
            },
        };

        try {
            // Make the POST request to join the project
            const response = await axios.post('/projects/join', projectData, config);
            console.log('Joined project successfully:', response.data);
            navigate('/editor');  // Redirect to the editor after joining the project
        } catch (error) {
            console.error('Error joining project', error);
            setError(error.response?.data?.error || 'Error joining project');  // Set error if project joining fails
        }
    };

    return (
        <div>
            <h2>Create or Join a Project</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display error if present */}
            <input
                type="text"
                name="projectName"
                placeholder="Project Name"
                value={projectData.projectName}
                onChange={handleInputChange}
                required
            />
            <input
                type="password"
                name="pjpassword"  // Changed to pjpassword
                placeholder="Password"
                value={projectData.pjpassword}  // Changed to pjpassword
                onChange={handleInputChange}
                required
            />
            <button onClick={handleCreateProject}>Create Project</button>
            <button onClick={handleJoinProject}>Join Project</button>
        </div>
    );
};

export default Project;
