import React, { useState } from 'react';
import axios from '../services/axios';  // Ensure axios is correctly set up for API calls
import { useNavigate } from 'react-router-dom';  // For navigation after project creation/joining

const Project = () => {
    const [projectName, setProjectName] = useState('');
    const [pjpassword, setPjpassword] = useState('');

    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);  // For handling loading state
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        setProjectData({ ...projectData, [e.target.name]: e.target.value });
    };

    // Validate input
    const validateInput = () => {
        if (!projectData.projectName || !projectData.pjpassword) {
            setError('Project name and password are required');
            return false;
        }
        return true;
    };

    // Create Project (with token in the header)
    const handleCreateProject = async () => {
        if (!validateInput()) return;  // Validate input before proceeding

        const token = localStorage.getItem('token');
        const config = {
            headers: { 'Authorization': `Bearer ${token}` }
        };

        setLoading(true);  // Set loading state to true

        try {
            const response = await axios.post('/projects/create', projectData, config);
            console.log('Project created successfully:', response.data);
            navigate('/editor');
        } catch (error) {
            console.error('Error creating project:', error);
            setError(error.response?.data?.error || 'Error creating project');
        } finally {
            setLoading(false);  // Reset loading state
        }
    };

    // Join Project (with token in the header)
    const handleJoinProject = async () => {
        if (!validateInput()) return;  // Validate input before proceeding

        const token = localStorage.getItem('token');
        const config = {
            headers: { 'Authorization': `Bearer ${token}` }
        };

        setLoading(true);  // Set loading state to true

        try {
            const response = await axios.post('/projects/join', projectData, config);
            console.log('Joined project successfully:', response.data);
            navigate('/editor');
        } catch (error) {
            console.error('Error joining project:', error);
            setError(error.response?.data?.error || 'Error joining project');
        } finally {
            setLoading(false);  // Reset loading state
        }
    };

    return (
        <div>
            <h2>Create or Join a Project</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}  {/* Display error if present */}

            <input
                type="text"
                name="projectName"
                placeholder="Project Name"
                value={projectData.projectName}
                onChange={handleInputChange}
                required
            />
            <input
                type="password"
                name="pjpassword"
                placeholder="Password"
                value={projectData.pjpassword}
                onChange={handleInputChange}
                required
            />

            <button onClick={handleCreateProject} disabled={loading}>
                {loading ? 'Creating Project...' : 'Create Project'}
            </button>

            <button onClick={handleJoinProject} disabled={loading}>
                {loading ? 'Joining Project...' : 'Join Project'}
            </button>
        </div>
    );
};

export default Project;
